import styled from 'styled-components';

import { iconSizes } from './Icon.utils';
import * as tokens from '../tokens';

export const IconFrame = styled.div`
    display: flex;
    flex: 0;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100%;
    min-width: ${props => `${iconSizes[props.size]}px`};
    min-height: ${props => `${iconSizes[props.size]}px`};
`;

export const IconSVG = styled.svg`
    width: ${props => `${iconSizes[props.size]}px`};
    height: ${props => `${iconSizes[props.size]}px`};

    #Primary {
        fill: ${props =>
            props.color === 'more'
                ? props.primaryColor
                : props.color === 'dark'
                ? tokens.ColorNeutral._1000
                : tokens.ColorNeutral._0};
    }

    #Secondary {
        fill: ${props =>
            props.color === 'more'
                ? props.secondaryColor
                : props.color === 'dark'
                ? tokens.ColorNeutral._1000
                : tokens.ColorNeutral._0};
    }
`;
