import * as React from 'react';
import { PropertyControls, ControlType } from "framer";
import styled from 'styled-components';

import { 
    IIconProps,
    iconDefaultProps,
    iconPropertyControls,
    renderIcon
} from './Icon.base';

const initialSize = 'md';
const resizable = true;

export class Icon extends React.Component<IIconProps> {
    static defaultProps = iconDefaultProps(initialSize);
    static propertyControls: PropertyControls = iconPropertyControls(initialSize, resizable);

    render() {
        return renderIcon(this.props);
    }
}


