import * as React from 'react';
import { FrameProperties } from 'framer';

import * as tokens from './tokens';

const TokenContext = React.createContext(null);
const TokenProvider = TokenContext.Provider;
export const TokenConsumer = TokenContext.Consumer;

interface IQuantumProps extends FrameProperties {}

interface IQuantumState {
    tokens: any;
}

export class QuantumComponent extends React.Component<Partial<IQuantumProps>, IQuantumState> {
    state = {
        tokens
    };

    render() {
        return (
            <TokenProvider value={this.state.tokens}>{this.state.tokens ? this.props.children : null}</TokenProvider>
        );
    }
}
